class Vehicle {
  PVector location;
  PVector velocity;
  PVector acceleration;
  // Maximum speed
  float maxspeed;

void seek(PVector target) {
  PVector desired = PVector.sub(target,location);
  desired.normalize();
  // Calculating the desired velocity to target at max speed
  desired.mult(maxspeed);
  // Reynolds formula for steering force
  PVector steer = PVector.sub(desired,velocity);
  // Using our physics model and applying the force to the object’s acceleration
  applyForce(steer);
}

class Vehicle {
  PVector location;
  PVector velocity;
  PVector acceleration;
  float maxspeed;
  // Maximum force
  float maxforce;

void seek(PVector target) {
    PVector desired = PVector.sub(target,location);
    desired.normalize();
    desired.mult(maxspeed);
    PVector steer = PVector.sub(desired,velocity);

    // Limit the magnitude of the steering force
    steer.limit(maxforce);
    applyForce(steer);
  }

*Example 6-1: Seeking a Target*
class Vehicle {

  PVector location;
  PVector velocity;
  PVector acceleration;
  // Additional variable for size
  float r;
  float maxforce;
  float maxspeed;

  Vehicle(float x, float y) {
    acceleration = new PVector(0,0);
    velocity = new PVector(0,0);
    location = new PVector(x,y);
    r = 3.0;
    // Arbitrary values for maxspeed and force; try varying these!
    maxspeed = 4;
    maxforce = 0.1;
  }

  void update() {
    // Our standard “Euler integration” motion model
    velocity.add(acceleration);
    velocity.limit(maxspeed);
    location.add(velocity);
    acceleration.mult(0);
  }

  // Newton’s second law; we could divide by mass if we wanted
  void applyForce(PVector force) {
    acceleration.add(force);
  }

  // Our seek steering force algorithm
  void seek(PVector target) {
    PVector desired = PVector.sub(target,location);
    desired.normalize();
    desired.mult(maxspeed);
    PVector steer = PVector.sub(desired,velocity);
    steer.limit(maxforce);
    applyForce(steer);
  }

  void display() {
        // Vehicle is a triangle pointing in the direction of velocity;
        // since it is drawn pointing up, we rotate it an additional 90  degrees
    float theta = velocity.heading2D() + PI/2;
    fill(175);
    stroke(0);
    pushMatrix();
    translate(location.x,location.y);
    rotate(theta);
    beginShape();
    vertex(0, -r*2);
    vertex(-r, r*2);
    vertex(r, r*2);
    endShape(CLOSE);
    popMatrix();
  }
